
public class MathDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
       System.out.println(Math.abs(-100));//100,-100的绝对值。
       System.out.println(Math.PI); //返回圆周率
       System.out.println(Math.E); //返回E指数
       System.out.println(Math.ceil(3.156));//向上取整
       System.out.println(Math.floor(3.156));//向下取整
       System.out.println(Math.round(3.55678));//四舍五入取整
       System.out.println(Math.sqrt(100));//100的平方根
       System.out.println(Math.pow(2, 4));//2的4次方  -->16
       System.out.println(Math.random());//生成一个0-1之间的随机小数。
	}

}
