
public class Test3 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
       
		int n = 97;
		//转换为二进制数
		System.out.println(Integer.toBinaryString(n));
		//转换为八进制数
		System.out.println(Integer.toOctalString(n));
		//转换为十六进制数
		System.out.println(Integer.toHexString(n));
	}

}
